import React, { useState, useEffect } from 'react';
import API from '../api';
import HabitList from '../components/HabitList';
import CreateHabit from '../components/CreateHabit';
import FilterHabits from '../components/FilterHabits';
import Logout from '../components/Logout';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [habits, setHabits] = useState([]);
  const [filteredHabits, setFilteredHabits] = useState([]);
  const [isFiltered, setIsFiltered] = useState(false);
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');
  const authHeader = { headers: { Authorization: `Bearer ${token}` } };

  const fetchUser = async () => {
    try {
      const res = await API.get('/users/getUser', authHeader);
      setUser(res.data);
    } catch (err) {
      setError('Failed to fetch user');
    }
  };

  const fetchHabits = async () => {
    try {
      const res = await API.get('/habits/getHabits', authHeader);
      setHabits(res.data);
      setFilteredHabits([]);
      setIsFiltered(false);
    } catch (err) {
      setError('Failed to fetch habits');
    }
  };

  const handleFilter = (results) => {
    setFilteredHabits(results);
    setIsFiltered(true);
  };

  const handleCompletion = async (habitId) => {
    try {
      await API.put(`/habits/update/${habitId}`, { completed: true }, authHeader);
      await fetchUser(); // update points
      await fetchHabits();
    } catch (err) {
      setError('Failed to update habit');
    }
  };

  useEffect(() => {
    if (!token) {
      setError('You are not logged in.');
      return;
    }
    fetchUser();
    fetchHabits();
  }, []);

  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!user) return <p>Loading...</p>;

  return (
    <div>
      <h1>Welcome, {user.username}!</h1>
      <p>Points: {user.points}</p>

      <FilterHabits setFilteredHabits={handleFilter} />
      {isFiltered && (
        <button onClick={() => { setIsFiltered(false); }} className="bg-red-500 text-white px-2 py-1 rounded">
          Clear Filters
        </button>
      )}

      <HabitList
        habits={isFiltered ? filteredHabits : habits}
        onHabitCompletion={handleCompletion}
        refreshHabits={fetchHabits}
      />

      <CreateHabit refreshHabits={fetchHabits} />
      <Logout />
    </div>
  );
};

export default Dashboard;
