import React, { useEffect, useState } from 'react';
import { Doughnut } from 'react-chartjs-2';
import axios from 'axios';
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const UserRingChart = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return;

        const response = await axios.get('http://localhost:5000/api/users/getUser', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        setUserData(response.data);
      } catch (err) {
        console.error('Failed to fetch user:', err);
      }
    };

    fetchUser();
  }, []);

  if (!userData) return null; // Don't show chart if user isn't logged in

  const data = {
    labels: ['Points Earned', 'Remaining to Next Milestone (e.g., 500)'],
    datasets: [
      {
        data: [userData.points, Math.max(0, 500 - userData.points)],
        backgroundColor: ['#168d8f', '#e0e0e0'],
        borderWidth: 1,
      },
    ],
  };

  return (
    <div style={{ width: '300px', margin: 'auto' }}>
      <h3 style={{ textAlign: 'center' }}>{userData.username}'s Progress</h3>
      <Doughnut data={data} />
    </div>
  );
};

export default UserRingChart;
