import React, { useState } from 'react';

const FilterHabits = ({ onFilter }) => {
  const [status, setStatus] = useState('');
  const [frequency, setFrequency] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [sortBy, setSortBy] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Send the filter values to parent component
    onFilter({ status, frequency, startDate, endDate, sortBy });
  };

  return (
    <form onSubmit={handleSubmit} className="p-4 rounded-lg shadow-md bg-white space-y-4">
      <div>
        <label className="block font-medium">Status</label>
        <select
          className="w-full border p-2 rounded"
          value={status}
          onChange={(e) => setStatus(e.target.value)}
        >
          <option value="">All</option>
          <option value="done">Done</option>
          <option value="pending">Pending</option>
        </select>
      </div>

      <div>
        <label className="block font-medium">Frequency</label>
        <select
          className="w-full border p-2 rounded"
          value={frequency}
          onChange={(e) => setFrequency(e.target.value)}
        >
          <option value="">All</option>
          <option value="daily">Daily</option>
          <option value="weekly">Weekly</option>
          <option value="monthly">Monthly</option>
        </select>
      </div>

      <div className="flex flex-col gap-2">
        <label className="font-medium">Date Range</label>
        <input
          type="date"
          className="border p-2 rounded"
          value={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
        <input
          type="date"
          className="border p-2 rounded"
          value={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
      </div>

      <div>
        <label className="block font-medium">Sort By</label>
        <select
          className="w-full border p-2 rounded"
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
        >
          <option value="">None</option>
          <option value="name">Name (A-Z)</option>
        </select>
      </div>

      <button
        type="submit"
        className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
      >
        Apply Filters
      </button>
    </form>
  );
};

export default FilterHabits;
