import React, { useState, useEffect } from 'react';
import API from '../api';
import HabitList from '../components/HabitList';
import CreateHabit from '../components/CreateHabit';
import FilterHabits from '../components/FilterHabits';

const Dashboard = () => {
  console.log('Dashboard mounted!');

  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [hasWelcomed, setHasWelcomed] = useState(false);
  const [habits, setHabits] = useState([]);
  const [filteredHabits, setFilteredHabits] = useState([]);
  const [isFiltered, setIsFiltered] = useState(false);

  // Fetch user data
  const fetchUserData = async () => {
    console.log('Fetching user data...');
    try {
      const response = await API.get('/users/getUser');
      if (response.data) {
        console.log('User data fetched:', response.data);
        setUser(response.data);
        if (!hasWelcomed && response.data?.username) {
          alert(`Welcome, ${response.data.username}!`);
          setHasWelcomed(true);
        }
      }
      setLoading(false);
    } catch (error) {
      console.log('Error fetching user data:', error);
      setError('Failed to fetch user data');
      setLoading(false);
    }
  };

  // Fetch all habits
  const fetchHabits = async () => {
    console.log('Fetching habits...');
    try {
      const response = await API.get('/habits/getHabits');
      if (response.data) {
        console.log('Habits fetched:', response.data);
        setHabits(response.data);
        setFilteredHabits([]); // Clear filtered habits on refetch
        setIsFiltered(false);   // Reset filter status on new fetch
      }
    } catch (error) {
      console.error('Error fetching habits:', error);
    }
  };

  // Refetch user data and habits when the component mounts
  useEffect(() => {
    console.log('useEffect triggered');
    const fetchData = async () => {
      await fetchUserData();
      await fetchHabits();
    };
    fetchData();
  }, []); // Empty dependency array ensures this runs only once on mount

  // When filters are applied
  const handleFilter = (filteredResults) => {
    console.log('Applying filter...');
    setFilteredHabits(filteredResults);
    setIsFiltered(true);
  };

  // Clear filters
  const clearFilters = () => {
    console.log('Clearing filters...');
    setIsFiltered(false);
    setFilteredHabits([]);
  };

  // Handle habit completion and points update
  const handleHabitCompletion = async (habitId) => {
    console.log('Updating habit completion...', habitId);
    try {
      // Update the habit completion status
      await API.put(`/habits/updateHabits/${habitId}`, { completed: true });

      // Refetch habits after completing one to get updated list
      fetchHabits();
      
      // Optionally refetch user data if you want points or other data updated
      fetchUserData();
    } catch (error) {
      console.error('Error updating habit:', error);
    }
  };

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <div>
          <h1>Hello, {user.username}!</h1>
          <p>Points: {user.points}</p> {/* Display user's updated points */}

          {/* Filter Component */}
          <FilterHabits setFilteredHabits={handleFilter} />

          {isFiltered && (
            <button 
              onClick={clearFilters} 
              className="bg-red-500 text-white px-4 py-2 mt-2 rounded">
              Clear Filters
            </button>
          )}

          <h2 className="mt-4">Your Habits</h2>

          {/* Show filtered or all habits */}
          <HabitList
            habits={isFiltered ? filteredHabits : habits}
            refreshHabits={fetchHabits}
            onHabitCompletion={handleHabitCompletion} // Pass habit completion handler
          />

          {/* Habit creation */}
          <CreateHabit refreshHabits={fetchHabits} />
        </div>
      )}
    </div>
  );
};

export default Dashboard;
