// src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import API from '../api'; // Importing API for requests
import HabitList from '../components/HabitList'; // Importing the HabitList component
import CreateHabit from '../components/CreateHabit';  // Import the CreateHabit component

const Dashboard = () => {
  const [user, setUser] = useState(null); // For storing the user's information
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [hasWelcomed, setHasWelcomed] = useState(false); // To prevent multiple alerts
  const [habits, setHabits] = useState([]); // For storing habits

  // Fetch user info
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await API.get('/api/users/getUser');
        setUser(response.data);
        setLoading(false);

        // Show welcome alert only once
        if (!hasWelcomed && response.data?.username) {
          alert(`Welcome, ${response.data.username}!`);
          setHasWelcomed(true);
        }
      } catch (error) {
        setError('Failed to fetch user data');
        setLoading(false);
      }
    };

    fetchUserData();
  }, [hasWelcomed]);

  // Fetch habits
  const fetchHabits = async () => {
    try {
      const response = await API.get('/api/habits/getHabits');
      setHabits(response.data);
    } catch (error) {
      console.error('Error fetching habits:', error);
    }
  };

  // Refresh habits when a new habit is created
  const refreshHabits = () => {
    fetchHabits();
  };

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <div>
          <h1>Hello, {user.username}!</h1>  {/* Display user's name */}
          <h2>Your Habits</h2>
          <HabitList habits={habits} refreshHabits={refreshHabits} />  {/* Pass habits and refresh function */}
          <CreateHabit refreshHabits={refreshHabits} />  {/* Pass refresh function to CreateHabit */}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
