import React, { useState, useEffect } from 'react';
import FilterHabits from './FilterHabits';
import axios from 'axios';

const HabitDashboard = () => {
  const [habits, setHabits] = useState([]);
  const [filters, setFilters] = useState({});

  const fetchFilteredHabits = async (filterParams) => {
    try {
      const query = new URLSearchParams(filterParams).toString(); // turn filters into query string
      const res = await axios.get(`/habits/filterHabits?${query}`); // your endpoint
      setHabits(res.data);
    } catch (error) {
      console.error('Error fetching filtered habits:', error.response?.data || error.message);
      setHabits([]); // Optional: show empty if not found
    }
  };

  const handleFilter = (filters) => {
    setFilters(filters);
    fetchFilteredHabits(filters); // Call when filters are applied
  };

  return (
    <div>
      <FilterHabits onFilter={handleFilter} />
      <div className="mt-6">
        {habits.length > 0 ? (
          habits.map((habit) => (
            <div key={habit._id} className="p-4 border rounded mb-2">
              <h3 className="font-bold">{habit.name}</h3>
              <p>{habit.description}</p>
              <p>Status: {habit.completed ? 'Done' : 'Pending'}</p>
              <p>Frequency: {habit.frequency}</p>
            </div>
          ))
        ) : (
          <p className="text-gray-500">No habits found.</p>
        )}
      </div>
    </div>
  );
};

export default HabitDashboard;
