import React, { useEffect, useState } from 'react';
import Dashboard from './Dashboard';

const Home = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  return (
    <div className="home-container">
      <div>
      {/* <div className="home-box"> */}
        {isLoggedIn ? (
          <Dashboard />
        ) : (
          <div style={{ padding: '2rem' }} className = "placer">
            <h1>Welcome to Routine Quest!</h1>
            <h4>Slay Procrastination like a boss!!</h4>
            <p>Your personal habit tracking and productivity booster tool.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
