import React, { useState, useEffect } from 'react';
import API from '../api';
import HabitList from '../components/HabitList';
import CreateHabit from '../components/CreateHabit';
import FilterHabits from '../components/FilterHabits';
import Logout from '../components/Logout';

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [habits, setHabits] = useState([]);
  const [filteredHabits, setFilteredHabits] = useState([]);
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [isFiltered, setIsFiltered] = useState(false);

  const token = localStorage.getItem('token');
  const authHeader = { headers: { Authorization: `Bearer ${token}` } };

  // Fetch user info
  const fetchUser = async () => {
    try {
      const res = await API.get('/users/getUser', authHeader);
      setUser(res.data);
    } catch (err) {
      setError('Unable to fetch user info');
    }
  };

  // Fetch all habits
  const fetchHabits = async () => {
    try {
      const res = await API.get('/habits/getHabits', authHeader);
      setHabits(res.data);
      setIsFiltered(false);
      setFilteredHabits([]);
    } catch (err) {
      setError('Unable to fetch habits');
    }
  };

  // Handle habit completion
  const handleCompletion = async (habitId) => {
    try {
      await API.put(`/habits/update/${habitId}`, { completed: true }, authHeader);
      await fetchUser();     // update points
      await fetchHabits();   // update habits
    } catch {
      setError('Failed to update habit');
    }
  };

  // Handle filter from FilterHabits component
  const handleFilter = (filtered) => {
    setFilteredHabits(filtered);
    setIsFiltered(true);
  };

  // Reset filters
  const clearFilters = () => {
    setFilteredHabits([]);
    setIsFiltered(false);
  };

  useEffect(() => {
    if (!token) {
      setError('You must be logged in to view the dashboard.');
      setIsLoading(false);
      return;
    }

    Promise.all([fetchUser(), fetchHabits()])
      .finally(() => setIsLoading(false));
  }, []);

  if (isLoading) return <p>Loading dashboard...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div className="p-4 max-w-3xl mx-auto">
      {/* User Section */}
      <div className="mb-6">
        <h1 className="text-2xl font-bold">Welcome, {user.username}!</h1>
        <p className="text-gray-700">Points: {user.points}</p>
      </div>

      {/* Filters and Clear */}
      <div className="mb-4">
        <FilterHabits setFilteredHabits={handleFilter} />
        {isFiltered && (
          <button
            className="bg-red-500 text-white px-3 py-1 mt-2 rounded"
            onClick={clearFilters}
          >
            Clear Filters
          </button>
        )}
      </div>

      {/* Habit List */}
      <div className="mb-6">
        <HabitList
          habits={isFiltered ? filteredHabits : habits}
          onHabitCompletion={handleCompletion}
          refreshHabits={fetchHabits}
        />
      </div>

      {/* Create New Habit */}
      <div className="mb-6">
        <CreateHabit refreshHabits={fetchHabits} />
      </div>

      {/* Logout */}
      <Logout />
    </div>
  );
};

export default Dashboard;
