// src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import API from '../api'; // Importing API for requests
import HabitList from '../components/HabitList'; // Importing the HabitList component
import CreateHabit from '../components/CreateHabit';  // Import the CreateHabit component

const Dashboard = () => {
  const [user, setUser] = useState(null); // For storing the user's information
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Fetch user info and habits
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await API.get('/api/user');  // Assuming you have a route that fetches user data
        setUser(response.data);
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch user data');
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <div>
          <h1>Hello, {user.username}!</h1>  {/* Display user's name */}
          <h2>Your Habits</h2>
          <HabitList />  {/* Habit list component that will display habits */}
          <CreateHabit />  {/* Button for creating a new habit */}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
