// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import API from '../api';
import CreateHabit from '../components/CreateHabit';
import HabitList from '../components/HabitList';
import FilterHabits from '../components/FilterHabits';

const Dashboard = () => {
  const [habits, setHabits] = useState([]);
  const [filters, setFilters] = useState({});

  // Fetch all habits for logged-in user
  const fetchHabits = async () => {
    try {
      const res = await API.get('/habits/getHabits');
      setHabits(res.data);
    } catch (error) {
      console.error('Error fetching habits:', error.response?.data || error.message);
      setHabits([]);
    }
  };

  // Fetch filtered habits based on user filters
  const fetchFilteredHabits = async (filterParams) => {
    try {
      const query = new URLSearchParams(filterParams).toString();
      const res = await API.get(`/habits/filterHabits?${query}`);
      setHabits(res.data);
    } catch (error) {
      console.error('Error fetching filtered habits:', error.response?.data || error.message);
      setHabits([]);
    }
  };

  const handleFilter = (filterValues) => {
    setFilters(filterValues);
    fetchFilteredHabits(filterValues);
  };

  useEffect(() => {
    fetchHabits(); // Initial fetch
  }, []);

  return (
    <div style={{ padding: '1rem' }}>
      <h1>Welcome to RoutineQuest Dashboard</h1>

      <CreateHabit refreshHabits={fetchHabits} />

      <hr />

      <h2>Your Habits</h2>

      <FilterHabits onFilter={handleFilter} />

      <HabitList habits={habits} refreshHabits={fetchHabits} />
    </div>
  );
};

export default Dashboard;
