// src/components/HabitList.js
import React, { useState, useEffect } from 'react';
import API from '../api'; 
import HabitItem from './HabitItem';  // Importing HabitItem to display individual habit

const HabitList = () => {
  const [habits, setHabits] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchHabits = async () => {
      try {
        const response = await API.get('/api/habits/getHabits');  // Adjusted URL
        setHabits(response.data);
      } catch (error) {
        console.error('Error fetching habits:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchHabits();
  }, []);

  if (loading) {
    return <p>Loading habits...</p>;
  }

  return (
    <div>
      {habits.length === 0 ? (
        <p>No habits found. Start adding some!</p>
      ) : (
        habits.map((habit) => (
          <HabitItem 
            key={habit._id} 
            habit={habit} 
            onUpdate={() => fetchHabits()}  // Refresh habit list after update
            onDelete={() => fetchHabits()}  // Refresh habit list after deletion
          />
        ))
      )}
    </div>
  );
};

export default HabitList;
