import React, { useEffect, useState } from 'react';
import Dashboard from './Dashboard';

const Home = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  return (
    <div>
      {isLoggedIn ? (
        <Dashboard />
      ) : (
        <div style={{ padding: '2rem' }}>
          <h1>Welcome to Routine Quest!</h1>
          <p>Your personal habit tracking and productivity booster tool.</p>
        </div>
      )}
    </div>
  );
};

export default Home;
