import React, { useState, useEffect } from 'react';
import API from '../api'; // Importing API for requests

const HabitItem = () => {
  const [habits, setHabits] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const handleDeleteHabit = async (habitId) => {
    try {
      await API.delete(`/habits/deleteHabits/${habitId}`);
      setHabits(habits.filter(habit => habit._id !== habitId));
    } catch (error) {
      setError('Failed to delete habit');
    }
  };

  const handleMarkComplete = async (habitId) => {
    try {
      await API.put(`/habits/updateHabits/${habitId}`, { completed: true });
      setHabits(habits.map(habit => 
        habit._id === habitId ? { ...habit, completed: true } : habit
      ));
    } catch (error) {
      setError('Failed to update habit');
    }
  };

  return (
    <div>
      <h1>Your Dashboard</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p style={{ color: 'red' }}>{error}</p>
      ) : (
        <div>
          <ul>
            {habits.map((habit) => (
              <li key={habit._id}>
                <h3>{habit.name}</h3>
                <p>{habit.description}</p>
                <p>Frequency: {habit.frequency}</p>
                <p>Status: {habit.completed ? 'Completed' : 'Pending'}</p>
                <button onClick={() => handleMarkComplete(habit._id)}>
                  Mark as Completed
                </button>
                <button onClick={() => handleDeleteHabit(habit._id)}>
                  Delete Habit
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default HabitItem;
